mapper.select:
SELECT

FROM am_operate_log
WHERE
	1=1

AND
	entity_id = #{entityId,jdbcType=INTEGER}


AND
	module_code = #{moduleCode,jdbcType=INTEGER}


AND
	operate_type = #{operateType,jdbcType=INTEGER}

ORDER BY operate_time DESC
LIMIT #{pageOffset},#{pageSize}

SELECT COUNT(1) FROM am_operate_log
WHERE
1=1

AND
	entity_id = #{entityId,jdbcType=INTEGER}


	AND
	module_code = #{moduleCode,jdbcType=INTEGER}


	AND
	operate_type = #{operateType,jdbcType=INTEGER}

SELECT
FROM am_operate_log WHERE 1=1
AND entity_id = #{entityId,jdbcType=INTEGER}
AND module_code = #{moduleCode,jdbcType=INTEGER}
AND operate_type = #{operateType,jdbcType=INTEGER}
ORDER BY operate_time DESC
